export declare type Callback = (...args: any[]) => void;
export declare type ListenerMap = Map<Callback, boolean>;
export declare type EventMap = {
    [name: string]: ListenerMap;
};
export default class EventBus {
    events: EventMap;
    on(name: string, callback: Callback): this;
    once(name: string, callback: Callback): this;
    off(name: string, callback?: Callback): this;
    emit(name: string, ...args: any[]): this;
}
